mapper.select:
SELECT * FROM article
WHERE 1
  AND id = #{id}

SELECT A.*, SUM(IF(R.point > 0, R.point, 0)) AS temp_sumLikePoint FROM( SELECT A.*, M.nickname AS temp_writerName FROM article AS A LEFT JOIN member AS M ON A.memberId = M.id WHERE 1
AND A.boardId = #{boardId}
AND A.title LIKE CONCAT('%', #{searchKeyword}, '%')
AND A.body LIKE CONCAT('%', #{searchKeyword}, '%')
AND ( A.title LIKE CONCAT('%', #{searchKeyword}, '%') OR A.body LIKE CONCAT('%', #{searchKeyword}, '%') )
LIMIT #{startPage}, #{offset}
) AS A LEFT JOIN reaction AS R ON A.id = R.articleId GROUP BY A.id

mapper.update:
UPDATE article
SET hitCount = hitCount + 1
WHERE id = #{id}

mapper.select:
SELECT hitCount
FROM article
WHERE id = #{id}
